import { ViewIcon } from '@chakra-ui/icons';
import { Button, IconButton, Modal, ModalContent, ModalOverlay, useDisclosure, ModalBody, ModalHeader, Image, Text } from '@chakra-ui/react';
import React, { useContext } from 'react';
import { ChatState } from '../../Context/ChatProvider'; // Import the ChatState context

const ProfileModal = ({ children }) => {
  const { isOpen, onOpen, onClose } = useDisclosure();
  const { user } = ChatState(); // Use useContext to access the user object from the ChatState context

  return (
    <>
      {children ? (
        <span onClick={onOpen}>
          {children}
        </span>
      ) : (
        <IconButton
          d={{ base: 'flex' }}
          icon={<ViewIcon />}
          onClick={onOpen}
        />
      )}
      <Modal size="lg" isOpen={isOpen} onClose={onClose} isCentered>
        <ModalOverlay />
        <ModalContent>
          <ModalHeader
            fontSize="40px"
            fontFamily="Work sans"
            d="flex"
            justifyContent="center"
          >
            {user ? user.name : 'User'}
          </ModalHeader>
          <ModalBody
            d="flex"
            flexDir="column"
            alignItems="center"
            justifyContent="center"
          >
            <Image
              src={user.pic}
              borderRadius="full"
              boxSize="150px"
              alt={user.name}
            />
            <Text
              fontSize={{ base: '28px', md: '30px', fontFamily: 'Work sans' }}
            >
              Email: {user.email}
            </Text>
          </ModalBody>
          {/* Remove ModalCloseButton */}
          <Button colorScheme='blue' mt={4} onClick={onClose}>
            Close
          </Button>
        </ModalContent>
      </Modal>
    </>
  );
};

export default ProfileModal;
