import React from 'react'
import { ChatState } from '../../Context/ChatProvider'
import { Flex } from '@chakra-ui/react';

const UserListItem = (handleFunction) => {

    const {user} = ChatState();
  return (
    <>
    <Flex
    onClick={handleFunction}
    cursor= 'pointer'
    bg="#E8E8E8"
    _hover={{
        background: "#38B2AC",
        color: "white",
    }}
    w= "100%"
    d="flex"
    alignItems="center"
    color="black"
    px={3}
    py={2}
    mb={2}
    borderRadius="lg"
    >


    </Flex>
    </>
  )
}

export default UserListItem
