import React, { useEffect } from 'react';
import { ChatState } from '../../Context/ChatProvider';
import { Button, Flex, Stack, Text, useToast } from '@chakra-ui/react';
import { AddIcon } from '@chakra-ui/icons';
import axios from 'axios';
import ChatLoading from '../ChatLoading';
import { getSender } from '../../config/ChatLogics';
import GroupChatModal from './GroupChatModal';

const MyChats = ({fetchAgain}) => {
  const { user, setChats, selectedChat, setSelectedChat, chats } = ChatState();
  const toast = useToast();

  const fetchChats = async () => {
    try {
      const config = {
        headers: {
          Authorization: `Bearer ${user.token}`,
        },
      };
      const { data } = await axios.get('/api/chat', config);
      console.log(data);
      setChats(data);
    } catch (error) {
      toast({
        title: 'Error Occurred',
        description: 'Failed to load chat',
        status: 'error',
        duration: 3000,
        isClosable: true,
        position: 'bottom-left',
      });
    }
  };

  useEffect(() => {
    fetchChats();
  }, [fetchAgain]);

  const chat = selectedChat !== null;

  return (
    <Flex
      direction="column"
      p={3}
      bg="white"
      w="40%"
      borderRadius="lg"
      borderWidth="1px"
      boxShadow="md"
      justifyContent="space-between"
      alignItems="stretch" // Ensure both columns have the same height
    >
      <Flex
        fontSize={{ base: '28px', md: '30px' }}
        fontFamily="Work Sans"
        borderBottom="1px solid gray"
        flex="1" // Allow text to take available space
        display="flex"
        alignItems="center"
      >
        My Chats

        <GroupChatModal>
         <Button
        d="flex"
        fontSize={{ base: '17px', md: '10px', lg: '17px' }}
        rightIcon={<AddIcon />}
        justifyContent="flex-end"
        flex="1" // Allow button to take available space
      >
        New Group Chat
      </Button>
      </GroupChatModal>
      </Flex>
      

      <Flex
        d="flex"
        flexDir="column"
        p={3}
        bg="#F8F8F8"
        w="100%"
        borderRadius="lg"
        overflow="hidden"
        flex="2" // Allow this column to take more space
      >
        {chat ? (
          <Stack overflowY="scroll">
            {chats.map((chat) => (
              <Flex
                onClick={() => setSelectedChat(chat)}
                cursor="pointer"
                bg={selectedChat === chat ? "#38B2AC" : "#E8E8E8"}
                color={selectedChat === chat ? "white" : "black"}
                px={3}
                py={2}
                borderRadius="lg"
                key={chat._id}
              >
                <Text>
                  {!chat.isGroupChat ? getSender(user, chat.users) : chat.chatName}
                </Text>
              </Flex>
            ))}
          </Stack>
        ) : (
          <ChatLoading />
        )}
      </Flex>
    </Flex>
  );
};

export default MyChats;
