import React, { useState } from 'react'
import { ViewIcon } from '@chakra-ui/icons';
import { Button, Icon, IconButton, Modal, ModalBody, ModalCloseButton, ModalContent, ModalFooter, ModalHeader, ModalOverlay, useDisclosure,Text, useToast, Box, FormControl, Input } from '@chakra-ui/react';

import { ChatState } from '../../Context/ChatProvider';
import UserBadgeItem from "../UserAvatar/UserBadgeItem"

const UpdateGroupChatModal = (fetchAgain,setFetchAgain) => {

    const { isOpen, onOpen, onClose } = useDisclosure();
    const {selectedChat,setSelectedChat,user}= ChatState();
    const [groupChatName,setgroupChatName]= useState();
    const [search, setSearch] = useState("");
  const [searchResult, setSearchResult] = useState([]);
  const [loading, setLoading] = useState(false);
  const [renameloading, setRenameLoading] = useState(false);
  const toast = useToast();

const handleRemove= ()=>{

}

const handleRename= ()=>{

}

const handleSearch= ()=>{

}


  return (
    <>
      <IconButton d="flex" icon={ViewIcon} onClick={onOpen} />


      <Modal isOpen={isOpen} onClose={onClose} isCentered>
        <ModalOverlay />
        <ModalContent>
          <ModalHeader
          fontSize="35px"
            fontFamily="Work sans"
            d="flex"
            justifyContent="center"
          >{selectedChat.chatName}</ModalHeader>
          <ModalCloseButton />
          <ModalBody
          d="flex" flexDir="column" alignItems="center"
          >
            <Box w="100%" d="flex" flexWrap="wrap" pb={3}>
              {selectedChat.users.map((u) => (
                <UserBadgeItem
                  key={u._id}
                  user={u}
                  admin={selectedChat.groupAdmin}
                  handleFunction={() => handleRemove(u)}
                />
              ))}
            </Box>
                <FormControl d="flex">
              <Input
                placeholder="Chat Name"
                mb={3}
                value={groupChatName}
                onChange={(e) => setgroupChatName(e.target.value)}
              />
              <Button
                variant="solid"
                colorScheme="teal"
                ml={1}
                isLoading={renameloading}
                onClick={handleRename}
              >
                Update
              </Button>
            </FormControl>

            <FormControl>
              <Input
                placeholder="Add User to group"
                mb={1}
                onChange={(e) => handleSearch(e.target.value)}
              />
            </FormControl>
            
          </ModalBody>

          <ModalFooter>
            <Button onClick={() => handleRemove(user)} colorScheme="red">
              Leave Group
            </Button>
          </ModalFooter>
        </ModalContent>
      </Modal>
    </>
  )
}

export default UpdateGroupChatModal
