import { ViewIcon } from '@chakra-ui/icons';
import { Button, IconButton, Modal, ModalCloseButton, ModalContent, ModalOverlay, useDisclosure, ModalBody, ModalFooter, ModalHeader,Image, Text } from '@chakra-ui/react';
import React, { useContext } from 'react';
import { ChatState } from '../../Context/ChatProvider'; // Import the ChatState context

const ProfileModal = ({ children }) => {
  const { isOpen, onOpen, onClose } = useDisclosure();
  const { user } = ChatState(); // Use useContext to access the user object from the ChatState context

  return (
    <>
      {children ? (
        <span onClick={onOpen}>
          {children}
        </span>
      ) : (
        <IconButton
          d={{ base: 'flex' }}
          icon={<ViewIcon />}
          onClick={onOpen}
        />
      )}
      <Modal isOpen={isOpen} onClose={onClose}>
        <ModalOverlay />
        <ModalContent>
          <ModalHeader fontSize="40px"
          fontFamily="Work sans"
          d="flex"
          justifyContent= "center"	
          >{user ? user.name : 'User'}</ModalHeader>
          <ModalCloseButton />
          <ModalBody 
          d="flex"
          flexDir="column"
          align
          >
            <Image
            src={user.pic}
            borderRadius="full"
            boxSize="150px"
            alt={user.name}
            />

            <Text fontSize={{ base: '28px', md: '30px', fontFamily: 'Work sans' }}>
          Email: {user.email}
        </Text>
          </ModalBody>

          <ModalFooter>
            <Button colorScheme='blue' mr={3} onClick={onClose}>
              Close
            </Button>
            <Button variant='ghost'>Secondary Action</Button>
          </ModalFooter>
        </ModalContent>
      </Modal>
    </>
  );
};

export default ProfileModal;
