import React from 'react';
import { Avatar, Box, Flex } from '@chakra-ui/react';

const UserListItem = ({ user, handleFunction }) => {
  return (
    <Flex
      onClick={handleFunction}
      cursor="pointer"
      bg="#E8E8E8"
      _hover={{
        background: "#38B2AC",
        color: "white",
      }}
      w="100%"
      alignItems="center"
      color="black"
      px={3}
      py={2}
      mb={2}
      borderRadius="lg"
    >
      <Avatar
        mr={2}
        size="sm"
        cursor="pointer"
        name={user.name}
        src={user.pic}
      />
      <Box>
        <Box fontWeight="bold">{user.name}</Box>
        <Box fontSize="sm">{user.email}</Box>
      </Box>
    </Flex>
  );
};

export default UserListItem;
