import React, { useEffect } from 'react';
import { ChatState } from '../../Context/ChatProvider';
import { Button, Flex, Stack, Text, useToast } from '@chakra-ui/react';
import { AddIcon } from '@chakra-ui/icons';
import axios from 'axios';
import ChatLoading from '../ChatLoading';
import { getSender } from '../../config/ChatLogics';

const MyChats = () => {
  const { user, setChats, selectedChat, setSelectedChat, chats } = ChatState();
  const toast = useToast();

  const fetchChats = async () => {
    try {
      const config = {
        headers: {
          Authorization: `Bearer ${user.token}`,
        },
      };
      const { data } = await axios.get('/api/chat', config);
      console.log(data);
      setChats(data);
    } catch (error) {
      toast({
        title: 'Error Occurred',
        description: 'Failed to load chat',
        status: 'error',
        duration: 3000,
        isClosable: true,
        position: 'bottom-left',
      });
    }
  };

  useEffect(() => {
    fetchChats();
  }, []);

  const chat = selectedChat !== null;

  return (
    <Flex
      direction="row"
      p={3}
      bg="white"
      w="100%"
      borderRadius="lg"
      borderWidth="1px"
      boxShadow="md"
      justifyContent="space-between"
    >
      <Flex
        fontSize={{ base: '28px', md: '30px' }}
        fontFamily="Work Sans"
        borderBottom="1px solid gray"
        flex="2" // Allow text to take more available space
        display="flex"
        
      >
        My Chats
      </Flex>
      <Flex
        d="flex"
        flexDir="column"
        flex="3" // Allow chat list column to take more space
        overflow="hidden"
      >
        <Flex
          direction="row" // Set the direction to "row"
          justifyContent="space-between" // Adjust the spacing
        >
          <Button
            d="flex"
            fontSize={{ base: '17px', md: '10px', lg: '17px' }}
            rightIcon={<AddIcon />}
          >
            New Group Chat
          </Button>
        </Flex>
        <Flex
          d="flex"
          flexDir="column"
          p={3}
          bg="#F8F8F8"
          w="100%"
          borderRadius="lg"
          overflow="hidden"
        >
          {chat ? (
            <Stack overflowY="scroll">
              {chats.map((chat) => (
                <Flex
                  onClick={() => setSelectedChat(chat)}
                  cursor="pointer"
                  bg={selectedChat === chat ? "#38B2AC" : "#E8E8E8"}
                  color={selectedChat === chat ? "white" : "black"}
                  px={3}
                  py={2}
                  borderRadius="lg"
                  key={chat._id}
                >
                  <Text>
                    {!chat.isGroupChat ? getSender(user, chat.users) : chat.chatName}
                  </Text>
                </Flex>
              ))}
            </Stack>
          ) : (
            <ChatLoading />
          )}
        </Flex>
      </Flex>
    </Flex>
  );
};

export default MyChats;
