import { FormControl, useToast, VStack, FormLabel, Input, InputGroup, InputRightElement, Button } from '@chakra-ui/react';
import React, { useState } from 'react';

const SignupPage = () => {
  const [show, setShow] = useState(false);
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [picture, setPicture] = useState(null); // Initialize picture state to null
  const [loading, setLoading] = useState(false); // Initialize loading state to false
  const toast = useToast();

  const handleClick = () => setShow(!show);

  const postDetails = (pics) => {
   setLoading()
    setPicture(pics); // Update the picture state with the selected file
  };

  const submitHandler = () => {
    if (!name || !email || !password || !confirmPassword || !picture) {
      toast({
        title: 'Please fill in all fields and upload your picture',
        status: 'warning',
        duration: 5000,
        isClosable: true,
        position: 'bottom',
      });
    } else {
      // You can proceed with your form submission logic here
      // Make an API request or perform any necessary actions
    }
  };

  return (
    <VStack spacing="5px" color="black">
      <FormControl id="first-name" isRequired>
        <FormLabel>Name</FormLabel>
        <Input
          placeholder="Enter your name"
          onChange={(e) => setName(e.target.value)}
        />
      </FormControl>
      <FormControl id="email" isRequired>
        <FormLabel>Email</FormLabel>
        <Input
          placeholder="Enter your Email"
          onChange={(e) => setEmail(e.target.value)}
        />
      </FormControl>
      <FormControl id="password" isRequired>
        <FormLabel>Password</FormLabel>
        <InputGroup>
          <Input
            type={show ? 'text' : 'password'}
            placeholder="Enter your Password"
            onChange={(e) => setPassword(e.target.value)}
          />
          <InputRightElement width="4.5rem">
            <Button h="1.75rem" size="sm" onClick={handleClick}>
              {show ? 'Hide' : 'Show'}
            </Button>
          </InputRightElement>
        </InputGroup>
      </FormControl>
      <FormControl id="pic" isRequired>
        <FormLabel>Upload your Picture</FormLabel>
        <Input
          type="file"
          p={1.5}
          accept="image/*"
          onChange={(e) => postDetails(e.target.files[0])}
          placeholder="Enter your Email"
        />
      </FormControl>
      <Button
        colorScheme="blue"
        width="100%"
        style={{ marginTop: 15 }}
        onClick={submitHandler}
      >
        Signup
      </Button>
    </VStack>
  );
};

export default SignupPage;
