import { Avatar, Button, Flex, Menu, MenuButton, Text, Tooltip,MenuList, MenuItem, Drawer, DrawerOverlay, DrawerHeader, DrawerContent,useDisclosure, DrawerBody, Input } from '@chakra-ui/react';
import { BellIcon, ChevronDownIcon} from '@chakra-ui/icons'
import {ChatState} from "../../Context/ChatProvider"

import React, { useState } from 'react';
import ProfileModal from './ProfileModal';
import { useHistory } from 'react-router-dom';

const SideDrawer = () => {
const [search,setSearch]= useState("");
const [searchResult,setSearchResult]= useState([]);
const [loading,setLoading]= useState(false);
const [loadingChat,setLoadingChat]= useState();
const {user}= ChatState();
const history= useHistory()
const {isOpen, onOpen, onClose}= useDisclosure();

const logoutHandler= () => {
  localStorage.removeItem("userInfo");
  history.push("/");
}
const handleOpenDrawer=()=>{
console.log("handleOpenDrawer")
onOpen();
}
console.log(user);

  return (
    <>
        <Flex
        d="flex"
        justifyContent="space-between"
        alignItems="center"
        bg="white"
        w="100%"
        p="5px 10px 5px 10px"
        borderWidth="5px"
        >

        
      <Tooltip
      label="Search Users to chat"
      hasArrow
      placement="bottom-end"
      >
        <Button variant="ghost" onClick={handleOpenDrawer}>
        <i className="fas fa-search"></i>
        <Text d={{ base: "none", md: "flex" }} px="4">
          Search User
        </Text>
      </Button>

      </Tooltip>
      <Text fontSize="2xl" fontFamily="Work sans">
        MERN-CHAT-APP
      </Text>
      <div>
        <Menu>
          <MenuButton p={1}>
          <BellIcon fontSize="2xl" m={1}/>
          </MenuButton>
        
        </Menu>
        <Menu>
          <MenuButton as={Button} rightIcon={<ChevronDownIcon/>}>
          <Avatar size="sm" cursor="pointer" name={user.name} src={user.pic}/>
          </MenuButton>
          <MenuList>
            <ProfileModal>
          <MenuItem>My Profile</MenuItem>
            </ProfileModal>
          
          <MenuItem onClick={logoutHandler}>Logout</MenuItem>
          </MenuList>
        </Menu>
      </div>
      </Flex>
       
      <Drawer placement='left' onClose={onClose} isOpen={isOpen}>
        <DrawerOverlay />
        <DrawerContent>
          <DrawerHeader borderBottomWidth='1px'>Search Users</DrawerHeader>
          <DrawerBody>
            <Flex pb={2}>
              <Input
              placeholder= ""
              />

            </Flex>
          </DrawerBody>
        </DrawerContent>
      </Drawer>

    </>
  );
};

export default SideDrawer;
