const isDocument = (el) => el.nodeType === Node.DOCUMENT_NODE;
function getDocument(el) {
  if (isDocument(el))
    return el;
  return el?.ownerDocument ?? document;
}
function getWindow(el) {
  return el?.ownerDocument.defaultView ?? window;
}

export { getDocument, getWindow };
