export const getSender = (loggedUser, user) => {
  console.log('loggedUser:', loggedUser);
  console.log('user:', user);

  if (loggedUser && user && loggedUser._id && user._id) {
    console.log('Accessing sender name...');
    return user._id === loggedUser._id ? loggedUser.name : user.name;
  } else {
    console.log('Unable to access sender name. Returning "Unknown Sender"');
    return 'Unknown Sender';
  }
};
