import React, { useEffect } from 'react';
import { ChatState } from '../../Context/ChatProvider';
import { Button, Flex, Stack, useToast } from '@chakra-ui/react';
import { AddIcon } from '@chakra-ui/icons';
import axios from 'axios';
import ChatLoading from '../ChatLoading';
import { getSender } from '../../config/ChatLogics';

const MyChats = () => {
  const { user, setChats, selectedChat,setSelectedChat,chats } = ChatState();
  const toast = useToast();

  const fetchChats = async () => {
    try {
      const config = {
        headers: {
          Authorization: `Bearer ${user.token}`,
        },
      };
      const { data } = await axios.get('/api/chat', config);
      console.log(data);
      setChats(data);
    } catch (error) {
      toast({
        title: 'Error Occurred',
        description: 'Failed to load chat',
        status: 'error',
        duration: 3000,
        isClosable: true,
        position: 'bottom-left',
      });
    }
  };

  useEffect(() => {
    fetchChats();
  }, []);

  return (
    <Flex
      direction="row" // Set the direction to "row"
      
      p={3}
      bg="white"
      w="100%" // Set the width to 100%
      borderRadius="lg"
      borderWidth="1px"
      boxShadow="md" // Add a shadow
      justifyContent="space-between" // Adjust the spacing
    >
      <Flex
        fontSize={{ base: '28px', md: '30px' }}
        fontFamily="Work Sans"
        
        borderBottom="1px solid gray" // Add a border
      >
        My Chats
      </Flex>
      <Button
        d="flex"
        fontSize={{ base: '17px', md: '10px', lg: '17px' }}
        rightIcon={<AddIcon />}
      >
        New Group Chat
      </Button>

      <Flex 
      d="flex"
      flexDir="column"
      p={3}
      bg="#F8F8F8"
      w="100%"
      h="100%"
      borderRadius="lg"
      overflow="hidden"
      >
      {chat ? (
        <Stack overflowY="scroll">
          {chats.map((chat)=>(
            <Flex
            onClick={()=> setSelectedChat(chat)}
            cursor="pointer"
            bg={selectedChat === chat ? "#38B2AC" : "#E8E8E8"}
            color={selectedChat === chat ? "white" : "black"}
            px={3}
            py={2}
            borderRadius="lg"
            key={chat._id}
            >
              <Text>
                {!chat.isGroupChat ? getSender(loggedUser,chat.user): chat.chatName}
              </Text>
            </Flex>
          ))}
        </Stack>
      ) : (
        <ChatLoading />
      )}
    </Flex>
    </Flex>
    
  );
};

export default MyChats;
