import React from 'react';
import { ChatState } from '../../Context/ChatProvider';
import { Flex, IconButton, Text, Box } from '@chakra-ui/react';
import { ArrowBackIcon } from "@chakra-ui/icons";
import { getSender, getSenderFull } from '../../config/ChatLogics';
import ProfileModal from './ProfileModal';
import UpdateGroupChatModal from './UpdateGroupChatModal';

const SingleChat = ({ fetchAgain, setFetchAgain }) => {
  const { selectedChat, setSelectedChat, user } = ChatState(); // Include 'user' from ChatState

  return (
    <>
      {selectedChat ? (
        <Flex
          flexDirection="column"
          alignItems="stretch"
          h="100vh"
          w="100%"
          overflow="hidden"
        >
          <Flex
            fontSize={{ base: "28px", md: "30px" }}
            pb={3}
            px={2}
            w="100%"
            fontFamily="Work sans"
            justifyContent="space-between"
            alignItems="center"
          >
            <IconButton
              d={{ base: "flex", md: "none" }}
              icon={<ArrowBackIcon />}
              onClick={() => setSelectedChat("")}
            />
            <Text>
              {selectedChat.isGroupChat
                ? selectedChat.chatName.toUpperCase()
                : getSender(user, selectedChat.users)} {/* Pass 'user' as a parameter */}
            </Text>

            {/* Wrap the user name and ProfileModal in a flex container */}
            <Flex alignItems="center">
              <ProfileModal user={getSenderFull(user, selectedChat.users)} />
              <UpdateGroupChatModal
                fetchAgain={fetchAgain}
                setFetchAgain={setFetchAgain}
              />
            </Flex>
          </Flex>

          <Box
            d="flex"
            flexDir="column"
            justifyContent="flex-end"
            p={3}
            bg="#E8E8E8"
            w="100%"
            h="450px" // Set an appropriate fixed height
            borderRadius="lg"
            overflowY="auto" // Use 'auto' to enable vertical scrolling
          >
            {/* Chat content goes here */}
            {/* You can map through and display the chat messages here */}
            {selectedChat.messages.map((message) => (
              <div key={message.id}>
                {/* Render each chat message */}
                <p>{message.content}</p>
              </div>
            ))}
          </Box>
        </Flex>
      ) : (
        <Flex
          d="flex"
          alignItems="center"
          justifyContent="center"
          h="100vh" // Use "100vh" for full viewport height
        >
          <Text fontSize="3xl" pb={3} fontFamily="Work sans">
            Click on a user to start a new conversation
          </Text>
        </Flex>
      )}
    </>
  );
};

export default SingleChat;
