import {createContext, useContext} from 'react'

const ChatContext = createContext()

const ChatProvider = ({children})=>{
    const [chat, setChat] = useState([]);
    const [user, setUser] = useState([]);
    const [room, setRoom] = useState([]);
    const [roomName, setRoomName] = useState('');
    const [message, setMessage] = useState('');
    const [isTyping, setIsTyping] = useState(false);
    const [isOnline, setIsOnline] = useState(false);
    const [onlineUsers, setOnlineUsers] = useState([]);
    const [onlineUsersCount, setOnlineUsersCount] = useState(0);
    const [onlineUsersList, setOnlineUsersList] = useState([]);
    const [onlineUsersListCount, setOnlineUsersListCount] = useState(0);
    const [onlineUsersRoom, setOnlineUsersRoom] = useState([]);
    const [onlineUsersRoomCount, setOnlineUsersRoomCount] = useState(0);
    const [onlineUsersRoomList, setOnlineUsersRoomList] = useState([]);
    return(
        <ChatContext.Provider >
            {children}
        </ChatContext.Provider>
    )
};

export const ChatState = ()=>{
    return useContext(ChatProvider);

};



export default  ChatProvider
