import { FormControl, useToast, VStack, FormLabel, Input, InputGroup, InputRightElement, Button } from '@chakra-ui/react';
import React, { useState } from 'react';

const SignupPage = () => {
  const [show, setShow] = useState(false);
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [picture, setPicture] = useState(null); // Initialize picture state to null
  const [loading, setLoading] = useState(false); // Initialize loading state to false
  const toast = useToast();

  const handleClick = () => setShow(!show);

  const postDetails = (pics) => {
    console.log('postDetails function called with image:', pics);
    // setLoading(true);
    setPicture(pics); // Update the picture state with the selected file
  };

  const submitHandler = async () => {
    console.log('submitHandler function called');
    if (!name || !email || !password || !confirmPassword || !picture) {
      toast({
        title: 'Please fill in all fields and upload your picture',
        status: 'warning',
        duration: 5000,
        isClosable: true,
        position: 'bottom',
      });
      setLoading(false);
      return;
    }

    if (password !== confirmPassword) {
      toast({
        title: 'Passwords do not match',
        status: 'error',
        duration: 5000,
        isClosable: true,
        position: 'bottom',
      });
      setLoading(false);
      return;
    }

    if (picture.type !== 'image/jpeg' && picture.type !== 'image/png') {
      toast({
        title: 'Unsupported image format. Please use JPEG or PNG',
        status: 'warning',
        duration: 5000,
        isClosable: true,
        position: 'bottom',
      });
      setLoading(false);
      return;
    }

    try {
      const data = new FormData();
      data.append('file', picture);
      data.append('upload_preset', 'chat-app');
      data.append('cloud_name', 'parosh');
      const response = await fetch(
        'https://api.cloudinary.com/v1_1/parosh/image/upload',
        {
          method: 'POST',
          body: data,
        }
      );
      if (!response.ok) {
        throw new Error('Error uploading image to Cloudinary');
      }
      const responseData = await response.json();
      console.log('Image upload success:', responseData);
      setPicture(responseData.secure_url); // Assuming you want to store the secure URL in the state
      setLoading(false);

      // Now you can proceed with the rest of your signup logic, e.g., sending the form data to your server.
    } catch (error) {
      console.error(error);
      toast({
        title: 'Error uploading image',
        status: 'error',
        duration: 5000,
        isClosable: true,
        position: 'bottom',
      });
      setLoading(false);
    }
  };

  return (
    <VStack spacing="5px" color="black">
      <FormControl id="first-name" isRequired>
        <FormLabel>Name</FormLabel>
        <Input
          placeholder="Enter your name"
          onChange={(e) => setName(e.target.value)}
        />
      </FormControl>
      <FormControl id="email" isRequired>
        <FormLabel>Email</FormLabel>
        <Input
          placeholder="Enter your Email"
          onChange={(e) => setEmail(e.target.value)}
        />
      </FormControl>
      <FormControl id="password" isRequired>
        <FormLabel>Password</FormLabel>
        <InputGroup>
          <Input
            type={show ? 'text' : 'password'}
            placeholder="Enter your Password"
            onChange={(e) => setPassword(e.target.value)}
          />
          <InputRightElement width="4.5rem">
            <Button h="1.75rem" size="sm" onClick={handleClick}>
              {show ? 'Hide' : 'Show'}
            </Button>
          </InputRightElement>
        </InputGroup>
      </FormControl>
      <FormControl id="confirm-password" isRequired>
        <FormLabel>Confirm Password</FormLabel>
        <Input
          type="password"
          placeholder="Confirm your Password"
          onChange={(e) => setConfirmPassword(e.target.value)}
        />
      </FormControl>
      <FormControl id="pic" isRequired>
        <FormLabel>Upload your Picture</FormLabel>
        <Input
          type="file"
          p={1.5}
          accept="image/jpeg, image/png"
          onChange={(e) => postDetails(e.target.files[0])}
        />
      </FormControl>
      <Button
        colorScheme="blue"
        width="100%"
        style={{ marginTop: 15 }}
        onClick={submitHandler}
        isLoading={loading}
      >
        Signup
      </Button>
    </VStack>
  );
};

export default SignupPage;
