import React, { useState } from 'react'
import { ViewIcon } from '@chakra-ui/icons';
import { Button, Icon, IconButton, Modal, ModalBody, ModalCloseButton, ModalContent, ModalFooter, ModalHeader, ModalOverlay, useDisclosure,Text, useToast, Box, FormControl, Input, Flex } from '@chakra-ui/react';

import { ChatState } from '../../Context/ChatProvider';
import UserBadgeItem from "../UserAvatar/UserBadgeItem"
import axios from 'axios';

const UpdateGroupChatModal = (fetchAgain,setFetchAgain) => {

    const { isOpen, onOpen, onClose } = useDisclosure();
    const {selectedChat,setSelectedChat,user}= ChatState();
    const [groupChatName,setgroupChatName]= useState();
    const [search, setSearch] = useState("");
  const [searchResult, setSearchResult] = useState([]);
  const [loading, setLoading] = useState(false);
  const [renameloading, setRenameLoading] = useState(false);
  const toast = useToast();

const handleRemove= ()=>{

}

const handleRename= async ()=>{
if(!groupChatName) return

try{
setRenameLoading(true);
const config = {
  headers: {
    Authorization: `Bearer ${user.token}`,

  },
};

const {data} = await axios.put("/api/chat/rename", config)

}catch(error){


}

}

const handleSearch= ()=>{

}


  return (
    <>
      <IconButton d="flex" icon={ViewIcon} onClick={onOpen} />


      <Modal isOpen={isOpen} onClose={onClose} isCentered>
        <ModalOverlay />
        <ModalContent>
          <ModalHeader
          fontSize="35px"
            fontFamily="Work sans"
            d="flex"
            justifyContent="center"
          >{selectedChat.chatName}</ModalHeader>
          <ModalCloseButton />
          <ModalBody d="flex" flexDir="column" alignItems="center">
  <Box w="100%" d="flex" flexWrap="wrap" pb={3}>
    {selectedChat.users.map((u) => (
      <UserBadgeItem
        key={u._id}
        user={u}
        admin={selectedChat.groupAdmin}
        handleFunction={() => handleRemove(u)}
      />
    ))}
  </Box>
  <Flex alignItems="center" mb={3}>
    <FormControl flex="1">
      <Input
        placeholder="Chat Name"
        value={groupChatName}
        onChange={(e) => setgroupChatName(e.target.value)}
      />
    </FormControl>
    <Button
      variant="solid"
      colorScheme="teal"
      ml={1}
      isLoading={renameloading}
      onClick={handleRename}
    >
      Update
    </Button>
  </Flex>
  <FormControl mb={1}>
    <Input
      placeholder="Add User to group"
      onChange={(e) => handleSearch(e.target.value)}
    />
  </FormControl>
</ModalBody>


          <ModalFooter>
            <Button onClick={() => handleRemove(user)} colorScheme="red">
              Leave Group
            </Button>
          </ModalFooter>
        </ModalContent>
      </Modal>
    </>
  )
}

export default UpdateGroupChatModal
