import { ViewIcon } from '@chakra-ui/icons';
import { IconButton, Modal, ModalOverlay, useDisclosure } from '@chakra-ui/react';
import React from 'react';

const ProfileModal = ({ user,children }) => { // Destructure children from props
  const { isOpen, onOpen, onClose } = useDisclosure();

  return (
    <>
      {children ? (
        <span onClick={onOpen}> {/* Changed OnOpen to onOpen */}
          {children}
        </span>
      ) : (
        <IconButton
          d={{ base: 'flex' }} 
          icon={<ViewIcon />}
          onClick={onOpen} 
        />
      )}
      <Modal isOpen={isOpen} onClose={onClose}>
        <ModalOverlay />
        <ModalContent>
          <ModalHeader>Modal Title</ModalHeader>
          <ModalCloseButton />
          <ModalBody>
            <Lorem count={2} />
          </ModalBody>

          <ModalFooter>
            <Button colorScheme='blue' mr={3} onClick={onClose}>
              Close
            </Button>
            <Button variant='ghost'>Secondary Action</Button>
          </ModalFooter>
        </ModalContent>
      </Modal>
    </>
  );
};

export default ProfileModal;
